import { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './App.css';
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from 'recharts';
import { toPng } from 'html-to-image';
import jsPDF from 'jspdf';
import { CSVLink } from 'react-csv';

function App() {
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [brand, setBrand] = useState('iPhone');
  const [images, setImages] = useState([]);
  const [analysis, setAnalysis] = useState({});
  const [brandSharpness, setBrandSharpness] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('All');
  const [comparison, setComparison] = useState({});
  const chartRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return alert('Please select an image');

    const formData = new FormData();
    formData.append('image', image);
    formData.append('brand', brand);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setMessage(res.data.message);
      setImage(null);
      fetchImages();
    } catch (err) {
      console.error(err);
      setMessage('Upload failed');
    }
  };

  const fetchImages = async () => {
    try {
      const res = await axios.get('http://localhost:5000/images');
      setImages(res.data);
    } catch (err) {
      console.error('Failed to fetch images', err);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const analyzeImage = async (img) => {
    try {
      const filenameWithoutPrefix = img.filename.replace(/^processed-/, '');
      const res = await axios.get(
        `http://localhost:5000/analyze/processed/${filenameWithoutPrefix}`
      );
      setAnalysis((prev) => ({
        ...prev,
        [img.filename]: res.data.metrics,
      }));
    } catch (err) {
      console.error('Analysis failed:', err);
      alert('Analysis failed.');
    }
  };

  const analyzeAllImages = async () => {
  const brandGroups = {};

  for (let img of images) {
    const filename = img.filename.replace(/^processed-/, '');
    try {
      const res = await axios.get(`http://localhost:5000/analyze/processed/${filename}`);
      const { variance, mean, contrastEstimate } = res.data.metrics;

      if (!brandGroups[img.brand]) {
        brandGroups[img.brand] = [];
      }

      brandGroups[img.brand].push({
        sharpness: parseFloat(variance),
        brightness: parseFloat(mean),
        contrast: parseFloat(contrastEstimate)
      });
    } catch (err) {
      console.error(`Error analyzing ${filename}:`, err.message);
    }
  }

  const result = Object.entries(brandGroups).map(([brand, entries]) => {
    const count = entries.length;
    const avgSharpness = entries.reduce((sum, x) => sum + x.sharpness, 0) / count;
    const avgBrightness = entries.reduce((sum, x) => sum + x.brightness, 0) / count;
    const avgContrast = entries.reduce((sum, x) => sum + x.contrast, 0) / count;

    return {
      brand,
      averageSharpness: parseFloat(avgSharpness.toFixed(2)),
      averageBrightness: parseFloat(avgBrightness.toFixed(2)),
      averageContrast: parseFloat(avgContrast.toFixed(2))
    };
  }).sort((a, b) => b.averageSharpness - a.averageSharpness);

  setBrandSharpness(result);
};
const compareWithBrandAverage = (img) => {
  const imgData = analysis[img.filename];
  if (!imgData) return;

  const brandData = brandSharpness.find(b => b.brand === img.brand);
  if (!brandData) return;

  const sharpnessDiff = imgData.variance - brandData.averageSharpness;
  const brightnessDiff = imgData.mean - brandData.averageBrightness;
  const contrastDiff = imgData.contrastEstimate - brandData.averageContrast;

  setComparison(prev => ({
    ...prev,
    [img.filename]: {
      sharpness: sharpnessDiff.toFixed(2),
      brightness: brightnessDiff.toFixed(2),
      contrast: contrastDiff.toFixed(2)
    }
  }));
};

  const downloadChartAsPNG = () => {
    if (chartRef.current === null) return;
    toPng(chartRef.current)
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = 'sharpness-chart.png';
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => {
        console.error('Error downloading PNG:', err);
      });
  };

  const downloadChartAsPDF = () => {
    if (chartRef.current === null) return;
    toPng(chartRef.current)
      .then((dataUrl) => {
        const pdf = new jsPDF();
        const imgProps = pdf.getImageProperties(dataUrl);
        const pdfWidth = pdf.internal.pageSize.getWidth();
        const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

        pdf.addImage(dataUrl, 'PNG', 0, 0, pdfWidth, pdfHeight);
        pdf.save('sharpness-chart.pdf');
      })
      .catch((err) => {
        console.error('Error downloading PDF:', err);
      });
  };

  const filteredImages = selectedBrand === 'All' ? images : images.filter(img => img.brand === selectedBrand);

  return (
    <div className="app-wrapper">
      <h1 className="title">üì± Smartphone Image Processor</h1>

      <form onSubmit={handleSubmit} className="form-container">
