import { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import './'
import {
  BarChart,
  Bar,
  XAxis,
  YAxis,
  Tooltip,
  CartesianGrid,
  ResponsiveContainer,
} from 'recharts';
import { toPng } from 'html-to-image';
import jsPDF from 'jspdf';
import { CSVLink } from 'react-csv';

function App() {
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [brand, setBrand] = useState('iPhone');
  const [images, setImages] = useState([]);
  const [analysis, setAnalysis] = useState({});
  const [brandSharpness, setBrandSharpness] = useState([]);
  const [selectedBrand, setSelectedBrand] = useState('All');
  const chartRef = useRef(null);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return alert('Please select an image');

    const formData = new FormData();
    formData.append('image', image);
    formData.append('brand', brand);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setMessage(res.data.message);
      setImage(null);
      fetchImages();
    } catch (err) {
      console.error(err);
      setMessage('Upload failed');
    }
  };

  const fetchImages = async () => {
    try {
      const res = await axios.get('http://localhost:5000/images');
      setImages(res.data);
    } catch (err) {
      console.error('Failed to fetch images', err);
    }
  };

  useEffect(() => {
    fetchImages();
  }, []);

  const analyzeImage = async (img) => {
    try {
      const filenameWithoutPrefix = img.filename.replace(/^processed-/, '');
      const res = await axios.get(
        `http://localhost:5000/analyze/processed/${filenameWithoutPrefix}`
      );
      setAnalysis((prev) => ({
        ...prev,
        [img.filename]: res.data.metrics,
      }));
    } catch (err) {
      console.error('Analysis failed:', err);
      alert('Analysis failed.');
    }
  };

  const analyzeAllImages = async () => {
    const brandGroups = {};

    for (let img of images) {
      const filename = img.filename;
      const filenameWithoutPrefix = filename.replace(/^processed-/, '');

      try {
        const res = await axios.get(
          `http://localhost:5000/analyze/processed/${filenameWithoutPrefix}`
        );
        const variance = parseFloat(res.data.metrics.variance);

        if (!brandGroups[img.brand]) {
          brandGroups[img.brand] = [];
        }

        brandGroups[img.brand].push(variance);
      } catch (err) {
        console.error(`Error analyzing ${filename}:`, err.message);
      }
    }

    const result = Object.entries(brandGroups).map(([brand, variances]) => {
      const avg = variances.reduce((sum, v) => sum + v, 0) / variances.length;
      return { brand, averageSharpness: parseFloat(avg.toFixed(2)) };
    }).sort((a, b) => b.averageSharpness - a.averageSharpness);

    setBrandSharpness(result);
  };

  const downloadChartAsPNG = () => {
    if (chartRef.current === null) return;
    toPng(chartRef.current)
      .then((dataUrl) => {
        const link = document.createElement('a');
        link.download = 'sharpness-chart.png';
        link.href = dataUrl;
        link.click();
      })
      .catch((err) => {
        console.error('Error downloading PNG:', err);
      });
  };

  const downloadChartAsPDF = () => {
    if (chartRef.current === null) return;
    toPng(chartRef.current)
      .then((dataUrl) => {
        const pdf = new jsPDF();
        const imgProps = pdf.getImageProperties(dataUrl);
        const pdfWidth = pdf.internal.pageSize.getWidth();
        const pdfHeight = (imgProps.height * pdfWidth) / imgProps.width;

        pdf.addImage(dataUrl, 'PNG', 0, 0, pdfWidth, pdfHeight);
        pdf.save('sharpness-chart.pdf');
      })
      .catch((err) => {
        console.error('Error downloading PDF:', err);
      });
  };

  const filteredImages = selectedBrand === 'All' ? images : images.filter(img => img.brand === selectedBrand);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-center mb-6">üì± Smartphone Image Processor</h1>

      <form
        onSubmit={handleSubmit}
        className="bg-white max-w-md mx-auto p-6 rounded-lg shadow space-y-4 mb-8"
      >
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="w-full border p-2 rounded"
        />
        <select
          onChange={(e) => setBrand(e.target.value)}
          className="w-full border p-2 rounded"
          value={brand}
        >
          <option value="iPhone">iPhone</option>
          <option value="Samsung">Samsung</option>
          <option value="Pixel">Pixel</option>
          <option value="Xiaomi">Xiaomi</option>
        </select>
        <button
          type="submit"
          className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Upload & Process
        </button>
        {message && <p className="text-green-700 font-semibold text-center">{message}</p>}
      </form>

      <div className="mb-6 flex gap-4 items-center">
        <button
          onClick={analyzeAllImages}
          className="bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
        >
          üìä Compare Sharpness by Brand
        </button>
        {brandSharpness.length > 0 && (
          <CSVLink
            data={brandSharpness}
            filename="sharpness-report.csv"
            className="bg-yellow-600 text-white px-4 py-2 rounded hover:bg-yellow-700"
          >
            üì• Export CSV
          </CSVLink>
        )}
        <select
          className="border p-2 rounded"
          value={selectedBrand}
          onChange={(e) => setSelectedBrand(e.target.value)}
        >
          <option value="All">All Brands</option>
          <option value="iPhone">iPhone</option>
          <option value="Samsung">Samsung</option>
          <option value="Pixel">Pixel</option>
          <option value="Xiaomi">Xiaomi</option>
        </select>
      </div>

      {brandSharpness.length > 0 && (
        <div className="bg-white p-6 rounded shadow mb-10" ref={chartRef}>
          <h2 className="text-xl font-semibold mb-4">üìä Average Sharpness by Brand</h2>
          <ResponsiveContainer width="100%" height={300}>
            <BarChart data={brandSharpness} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="brand" />
              <YAxis />
              <Tooltip />
              <Bar dataKey="averageSharpness" fill="#2563EB" />
            </BarChart>
          </ResponsiveContainer>
          <div className="flex gap-4 mt-4">
            <button
              onClick={downloadChartAsPNG}
              className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
            >
