import { useState } from 'react';
import axios from 'axios';

function App() {
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [brand, setBrand] = useState("iPhone");

  const handleSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append('image', image);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setMessage(res.data.message);
    } catch (err) {
      setMessage('Upload failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 flex flex-col items-center justify-center p-8">
      <h1 className="text-3xl font-bold mb-4">Smartphone Image Processor</h1>
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded-xl shadow-xl space-y-4">
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="border p-2 rounded"
        />
        <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700">
          Upload & Process
        </button>
        <select onChange={(e) => setBrand(e.target.value)} className="border p-2">
  <option>iPhone</option>
  <option>Samsung</option>
  <option>Pixel</option>
  <option>Xiaomi</option>
</select>
      </form>
      {message && <p className="mt-4 text-lg text-green-700">{message}</p>}
    </div>
  );
}

export default App;
