import { useEffect, useState } from 'react';
import axios from 'axios';
import { BarChart, Bar, XAxis, YAxis, Tooltip, CartesianGrid, ResponsiveContainer } from 'recharts';


function App() {
  const [image, setImage] = useState(null);
  const [message, setMessage] = useState('');
  const [brand, setBrand] = useState("iPhone");
  const [images, setImages] = useState([]);
  const [analysis, setAnalysis] = useState({});
  const [brandSharpness, setBrandSharpness] = useState([]);


  const analyzeAllImages = async () => {
  const brandGroups = {};

  for (let img of images) {
    const filename = img.filename;
    const filenameWithoutPrefix = filename.replace(/^processed-/, '');

    try {
      const res = await axios.get(`http://localhost:5000/analyze/processed/${filenameWithoutPrefix}`);
      const variance = parseFloat(res.data.metrics.variance);

      if (!brandGroups[img.brand]) {
        brandGroups[img.brand] = [];
      }

      brandGroups[img.brand].push(variance);
    } catch (err) {
      console.error(`Error analyzing ${filename}:`, err.message);
    }
  }

  // Calculate average per brand
  const result = Object.entries(brandGroups).map(([brand, variances]) => {
    const avg = variances.reduce((sum, v) => sum + v, 0) / variances.length;
    return { brand, averageSharpness: parseFloat(avg.toFixed(2)) };
  });

  setBrandSharpness(result);
};


  // Submit form
  const analyzeImage = async (img) => {
  try {
    const filenameWithoutPrefix = img.filename.replace(/^processed-/, '');
    const res = await axios.get(`http://localhost:5000/analyze/processed/${filenameWithoutPrefix}`);
    setAnalysis((prev) => ({
      ...prev,
      [img.filename]: res.data.metrics,
    }));
  } catch (err) {
    console.error('Analysis failed:', err);
    alert('Analysis failed.');
  }
};

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!image) return alert("Please select an image");

    const formData = new FormData();
    formData.append('image', image);
    formData.append('brand', brand);

    try {
      const res = await axios.post('http://localhost:5000/upload', formData);
      setMessage(res.data.message);
      setImage(null);
      fetchImages(); // Refresh gallery
    } catch (err) {
      console.error(err);
      setMessage('Upload failed');
    }
  };

  // Fetch images from server
  const fetchImages = async () => {
    try {
      const res = await axios.get('http://localhost:5000/images');
      setImages(res.data);
    } catch (err) {
      console.error("Failed to fetch images", err);
    }
  };


  // On mount, load existing images
  useEffect(() => {
    fetchImages();
  }, []);

  return (
    <div className="min-h-screen bg-gray-100 p-8">
      <h1 className="text-3xl font-bold text-center mb-6">📱 Smartphone Image Processor</h1>

      {/* Upload Form */}
      <form onSubmit={handleSubmit} className="bg-white max-w-md mx-auto p-6 rounded-lg shadow space-y-4 mb-8">
        <input
          type="file"
          onChange={(e) => setImage(e.target.files[0])}
          className="w-full border p-2 rounded"
        />
        <select
          onChange={(e) => setBrand(e.target.value)}
          className="w-full border p-2 rounded"
          value={brand}
        >
          <option value="iPhone">iPhone</option>
          <option value="Samsung">Samsung</option>
          <option value="Pixel">Pixel</option>
          <option value="Xiaomi">Xiaomi</option>
        </select>
        <button
  onClick={analyzeAllImages}
  className="mb-6 bg-green-600 text-white px-4 py-2 rounded hover:bg-green-700"
>
  📊 Compare Sharpness by Brand
</button>

        <button
          type="submit"
          className="w-full bg-blue-600 text-white px-4 py-2 rounded hover:bg-blue-700"
        >
          Upload & Process
        </button>
        {message && <p className="text-green-700 font-semibold text-center">{message}</p>}
      </form>

      {/* Gallery Display */}
      <h2 className="text-2xl font-semibold mb-4">🖼 Uploaded Images</h2>
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {images.map((img) => (
          <div key={img._id} className="bg-white p-4 rounded shadow">
            <h3 className="font-semibold text-lg">{img.brand}</h3>
            <p className="text-sm text-gray-500 mb-2">{new Date(img.processedAt).toLocaleString()}</p>
            <div className="flex space-x-2">
              <div className="w-1/2">
                <p className="text-xs text-gray-500 mb-1">Original</p>
                <img
                  src={`http://localhost:5000/uploads/${img.originalName}`}
                  alt="original"
                  className="rounded border"
                />
              </div>
              <div className="w-1/2">
                <p className="text-xs text-gray-500 mb-1">Processed</p>
                <img
                  src={`http://localhost:5000/uploads/${img.filename}`}
                  alt="processed"
                  className="rounded border"
                />
              </div>
              <div key={img._id} className="bg-white p-4 rounded shadow">
  <h3 className="font-semibold text-lg">{img.brand}</h3>
  <p className="text-sm text-gray-500 mb-2">{new Date(img.processedAt).toLocaleString()}</p>


    <button
    onClick={() => analyzeImage(img)}
    className="mt-3 text-sm bg-blue-500 text-white px-3 py-1 rounded hover:bg-blue-600"
  >
    Analyze Processed Image
  </button>

  {/* Show Analysis if available */}
  {analysis[img.filename] && (
    <div className="mt-2 text-sm text-gray-700">
      <p>📈 <strong>Sharpness:</strong> {analysis[img.filename].variance}</p>
      <p>🌗 <strong>Brightness:</strong> {analysis[img.filename].mean}</p>
      <p>🌓 <strong>Contrast Estimate:</strong> {analysis[img.filename].contrastEstimate}</p>
    </div>
  )}
  </div>
{brandSharpness.length > 0 && (
  <div className="bg-white p-6 rounded shadow mb-10">
    <h2 className="text-xl font-semibold mb-4">📊 Average Sharpness by Brand</h2>
    <ResponsiveContainer width="100%" height={300}>
      <BarChart data={brandSharpness} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
        <CartesianGrid strokeDasharray="3 3" />
        <XAxis dataKey="brand" />
        <YAxis />
        <Tooltip />
        <Bar dataKey="averageSharpness" fill="#2563EB" />
      </BarChart>
    </ResponsiveContainer>
  </div>
)}

  {/* Analyze Button */}



            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
